import React , {Component} from 'react';
import './Appointment.css'
import axios from 'axios';
import Swal from'sweetalert2';
import Table from 'react-bootstrap/Table'



class Appointment extends Component {
constructor(props){
  super(props)
  this.state={
    date:"",
    resdate:[],
    name:"",
    time:""
  }
}
deletApp=(appointId)=>{
Swal.fire({
title: 'Are you sure?',
text: "You won't be able to revert this!",
icon: 'warning',
showCancelButton: true,
confirmButtonColor: '#3085d6',
cancelButtonColor: '#d33',
confirmButtonText: 'Yes, delete it!'
}).then((result) => {
if (result.isConfirmed) {
this.deletAppoi(appointId)
}
})
}

deletAppoi=(appointId)=>{
    axios.delete("http://127.0.0.1:8000/appointments/"+appointId+"/")
    .then(response=>{
      if (response.data =! null){

     this.setState({
       resdate:this.state.resdate.filter(resdate=>resdate.id !== appointId)
     })
   }
 })
}

changeNameAppoi=(e)=>{
  this.setState({name:e.target.value})
}

changeTimeAppoi=(e)=>{
  this.setState({time:e.target.value})
}




dateChange=(e)=>{
  this.setState({date:e.target.value})
}

godate=()=>{
  axios.get("http://127.0.0.1:8000/appointments/?date="+this.state.date)
  .then((response)=>{
    console.log(response)
    this.setState({  resdate:response.data})
  })}


render(){
const{resdate,date}=this.state

return(
<div >
<div className="hederAppoin">
  <h3>Appointment list</h3>
  <div>
  <input  type ='date' name= 'date'value={date} onChange={this.dateChange}/>
<button className="searchDate" onClick={this.godate}>go</button>
</div>
  </div>
  <hr/>

        <Table  striped bordered hover>
    <thead>
      <tr className="tableHeader" tableHeader>

        <th colSpan="3"><div className='titlehederapp'><div>Patient</div><div className='fst' >Time</div></div></th>


      </tr>
    </thead>
    <tbody>
    {
      resdate.map(resdate=>
<tr key={resdate.id}>
   <td>{resdate.patient_name}</td>
   <td>{resdate.time}</td>
   <td><div className='icondeletApp'><i class="fas fa-trash-alt" onClick={this.deletApp.bind(this,resdate.id)}></i> </div></td>
      </tr>
      )
    }
    </tbody>
  </Table>


  <div className='add'>
     <form>
     <input type='text' placeholder='patient name' value={this.name} onChange={this.changeNameAppoi}/>
     <input type='time' value={this.time} onChange={this.changeTimeAppoi}/>

     </form>
  </div>
           </div>

)
}}
export default Appointment;
