import React , {Component} from 'react'
import Button from 'react-bootstrap/Button'
import Modal from 'react-bootstrap/Modal'
import axios from 'axios';
import {Link} from 'react-router-dom'
class Modall extends Component {
constructor(){
  super()
  this.state={
    posts:[],
    show:true
  }
}
add=(e)=>{

   const url = 'http://127.0.0.1:8000/patients/'
   const formData = new FormData();

   formData.append('name', document.querySelector('#name').value);
   formData.append('birthdate', document.querySelector('#birthdate').value);
   formData.append('wilaya', document.querySelector('#wilaya').value);
   formData.append('phone', document.querySelector('#phone').value);
   formData.append('photo', document.querySelector('#photo').files[0]);

   axios.post(url, formData)
   .then(resp => {
       console.log(resp.data);
       alert("new patient")

   });


}
handelModal(){
  this.setState({show:!this.state.show})
}
render(){
  const {posts}=this.state
  return (
  <div>
  <Button onClick={this.handelModal}>open modal</Button>
  <Modal
         show={this.show}
         onHide={this.handelModal}
         backdrop="static"
         keyboard={false}
       >
         <Modal.Header closeButton>
           <Modal.Title>Modal title</Modal.Title>
         </Modal.Header>
         <Modal.Body>
           I will not close if you click outside me. Don't even try to press
           escape key.
         </Modal.Body>
         <Modal.Footer>
           <Button variant="secondary" onClick={this.handelModal}>
             Close
           </Button>
           <Button variant="primary">Understood</Button>
         </Modal.Footer>
       </Modal>
  </div>
  );
}}
export default Modall;
