import React , {Component} from 'react';
import axios from 'axios';
class PostForm extends Component {


  constructor(props){
   super(props)
   this.state={
 name:"",
 birthdate:"",
 wilaya:"",
 phone:"",
photo:""

   }
 }
 uplodimage=(files)=>{
console.log.files[0]
}
handelerChange =(e)=>{
  this.setState({[e.target.name]: e.target.value})
}
submitHendler=(e)=>{

e.preventDefault();

console.log(this.state)
axios.post('http://127.0.0.1:8000/patients/', this.state)
.then(response=>{
  console.log(response)
})
.catch (err=>{
  console.log(err)
})

}
render (){
const {name,
birthdate,
wilaya,
phone,
photo,
}=this.state
return(
<div>
<form onSubmit={this.submitHendler}>
<div><input type="text" name="name" value={name}  onChange={this.handelerChange} />
</div>
<div><input type="date" name="birthdate"value={birthdate} onChange={this.handelerChange}/>
</div>
<div><input type="text" name="wilaya"value={wilaya} onChange={this.handelerChange}/>
</div>
<div><input type="number" name="phone"value={phone}onChange={this.handelerChange}/></div>
<div>
<input type="file" name="photo" value={photo} onChange={(event)=>{uplodimage(event.target.files)}}/>
</div>
<button type="submit">submit</button>
</form>
</div>
)


}

}
export default PostForm;
