import React,{Component} from 'react'
import axios from 'axios';

class PostForm extends Component {
  constructor(props){
    super(props)
    this.state={
         name:"",
         birthdate:"",
         wilaya:"",
         phone:"",
            }
  }

  hendelUserName=(e)=>{
    this.setState()
  }
  render(){
const {name,birthdate,wilaya,phone}=this.state
return(

<div>

<form>
    <input type='text'name='name' value={this.state.name} onChange={this.hendelUserName}/>
    <input type='date'name='birthdate' value={this.state.birthdate} onChange={this.hendelUserName}/>
    <input type='text'name='wilaya' value={this.state.wilaya} onChange={this.hendelUserName}/>
    <input type='text'name='phone' value={this.state.phone} onChange={this.hendelUserName}/>
    <button type='submit'>submit</button>
</form>

</div>


)


  }
}
export default PostForm;
