import React,{Component} from 'react'
import axios from 'axios';



 class File extends Component {
   constructor(props){
     super(props)
     this.state={

       name:"",
        birthdate:"",
       wilaya:"",
       phone:"",
       photo:""
     }
   }
   changeName=(e)=>{
        this.setState({name:e.target.value})
                          }
                          changePhone=(e)=>{
                               this.setState({phone:e.target.value})
                                                 }
   changebirthDate=(e)=>{
        this.setState({birthdate:e.target.value})
                               }
   changeWilaya=(e)=>{
       this.setState({wilaya:e.target.value})
                            }
                            changePhoto=(e)=>{
                                this.setState({photo:e.target.files[0]})
                                                     }
                                                     submitForm=(e)=>{
                                                       e.preventDefault()
                                                      console.log(this.state)
                                                       axios.post('http://127.0.0.1:8000/patients/',this.state)
                                                       .then(response=>{
                                                         console.log(response)
                                                       }) .catch(err=>{
                                                         console.log(err)
                                                       })


                                                     }


render(){
  return(

    <div className='add'>
    <form onSubmit={this.submitForm}>
<input type='text' name='name' value={this.name} onChange={this.changeName}/>
<input type='date' name='birthdate' value={this.birthdate} onChange={this.changeBithdate}/>
<input type='text' name='wilaya' value={this.wilaya} onChange={this.changeWilaya}/>
<input type='text' name='phone' value={this.phone} onChange={this.changePhone}/>
<input type='file' name='name' value={this.photo} onChange={this.changePhoto}/>
<button >submit</button>
</form>
    </div>
  )
}

 }
 export default File;
