import React,{Component} from 'react'
import axios from 'axios';

class PostForm extends Component {
  state ={
    selector:null
  }
  fileSelectHendler=(e)=>{
  this.setState(
      {selector: e.target.files[0]}
    )
  }
   aploadFileHendler=()=>{
    const fd = new FormData()
    fd.append('image',this.state.selector,this.state.selector.name)
    axios.posts('http://127.0.0.1:8000/patients/',fd)
  }
  render(){
    return(
      <div>
<input type='file' onChange={this.fileSelectHendler}/>
<button onClick={this.aploadFileHendler}>submit</button>
      </div>
    )
  }
}
export default PostForm;
