import React, { Component } from 'react'
import axios from 'axios';

class PrescAdd extends Component {
 state={
   pr:[],
 }
 add=(e)=>{

    const url = 'http://127.0.0.1:8000/prescription_fields/'
    const formData = new FormData();

    formData.append('quantity', document.querySelector('#quantity').value);
    formData.append('times_per_day', document.querySelector('#times_per_day').value);
    formData.append('consultation', document.querySelector('#consultation').value);
    formData.append('drug', document.querySelector('#drug').value);

    axios.post(url, formData)
    .then(resp => {
        console.log(resp.data);
        alert("new patient")

    });


}
  render(){
    const{pr}=this.state
    return(
      <div>
      <div className="input_field">
 <label for="quantity">quantity: </label>
        <input  className="input" type="text" id="quantity" required/><br/>
        </div>
        <div className="input_field">
 <label for="times_per_day">times_per_day: </label>
        <input className="input"type="text"  id="times_per_day"/><br/>
         </div>
        <div className="input_field">
 <label for="consultation">consultation: </label>
        <input className="input" type="text" id="consultation" /><br/>
         </div>
        <div className="input_field">
 <label for="drug:">drug: </label>
        <input className="input" type="text" id="drug" /><br/>
         </div>
         </div>
    )
  }
}
export default PrescAdd
