import React , {Component} from 'react'
import axios from 'axios';
class Patient extends Component {
  state={
     posts:[]

   }
  componentDidMount=()=>{
    axios.get("http://127.0.0.1:8000/patients/")
    .then((response)=>{
      console.log(response)
      this.setState({  posts:response.data})
    })}

render (){
  const {posts}= this.state

return(

<div>

<div className='cartPatient'>
{
posts.map(posts=>
  <ul className="patient"key={posts.id}>
      <li> <img className="imagePatent" src={posts.photo} /></li>
      <li className="name">{posts.name} <div className="wilaya">{posts.wilaya}</div> </li>
      <li className="phone"> <div className="phoneCouleur">phone</div> {posts.phone}</li>
      <li className="idPatient"> <div className="IdCouleur">ID</div>{posts.id}</li>
      <li className="icondelet"><i class="fas fa-trash-alt" onClick={this.handelDelet.bind(this,posts.id)}></i></li>

  </ul>)
}

</div>






</div>


)


}



}
export default Patient;
