import React ,{Component} from 'react'
class Tajrob extends Component {

  state={
     posts:[],
     c:"",
   }
  

apload=(e)=>{
  console.log(e.target.value)
}
   componentDidMount=()=>{
     axios.get("http://127.0.0.1:8000/patients/"+this.props.match.params.id+"/")
     .then((response)=>{
       console.log(response)
       this.setState({  posts:response.data})

     })}

render(){
    const {posts,c}= this.state

  return (

  <div>
  <input type="text" defaultValue={c} onChange={apload}/>
  <input type='button'/>
  <button onClick={this.apload}></button>
    </div>

  )}
}

export default Tajrob;
