import React,{Component} from 'react'
import axios from 'axios';

import Patients from './Patients'

class PostFormCons extends Component {
  constructor(props){
    super(props)
    this.state={
         date:"",
         type:"",
         description:"",
         outcome:"",
            }
  }

  hendeldate=(e)=>{
    this.setState({
      date:e.target.value
    })
  }
  hendeltype=(e)=>{
    this.setState({type:e.target.value})
  }
  hendeldescription=(e)=>{
    this.setState({description:e.target.value})
  }
  hendeloutcom=(e)=>{
    this.setState({outcome:e.target.value})
  }


  hendelSubmitt=(e)=>{

    e.preventDefault()
    console.log(this.state)
    axios.post('http://127.0.0.1:8000/patients/',this.state)

  }
  render(){
const {date,type,description,outcome}=this.state
return(

<div className='PostFormCons'>
<form onClick={this.hendelSubmitt}className='PostFormmCons' >
<div className='pst' >
   <label for='name'> date:</label>
    <input type='text'name='name' id='name' placeholder='name' value={this.state.name} onChange={this.hendeldate}/>
    </div>
    <div className='pst' >
    <label for='type'> type : </label>
    <input type='text'name='birthdate' value={this.state.birthdate} onChange={this.hendeltype}/>
    </div>
    <div className='pst' >
     <label for='desc'> description : </label>
    <input type='text'name='wilaya' value={this.state.wilaya} onChange={this.hendeldescription}/>
      </div>
      <div className='pst' >
    <label for='outcome'> outcome : </label>
    <input type='text'name='phone' value={this.state.phone} onChange={this.hendeloutcome}/>
   </div>
    <button type='submit' >submit</button>

</form>

</div>


)}
}
export default PostFormCons;
