import {Link} from "react-router-dom"


class AddDrug extends Component {

  state={
     drg:[],
   }
   changeCommercial_name=(e)=>{
     this.setState({commercial_name:e.target.value})
   }
   changeScientific_name=(e)=>{
     this.setState({scientific_name:e.target.value})
   }
   AddDrg=(e)=>{
     e.preventDefault()

     axios.post('http://127.0.0.1:8000/drugs/',this.state)
     .then(response=>{


     }) .catch(err=>{

     })
   }







render(){
const {drg}=this.state

return(

<div>


    <div className='addDrugform'>
    <div className="hededtDrug">
    <h2>add Drug</h2>

    </div>
     <form className='pstDRug' ><br/>
     <div className="input_fieldDrug">
<label for="commercial_name">commercial_name: </label>
        <input type='text' placeholder='Commercial name' name='commercial_name' id="commercial_name"
         value={this.commercial_name} onChange={this.changeCommercial_name}/><br/>
       </div>
       <div className="input_fieldDrug">
<label for="scientific_name">scientific_name: </label>
    <input  type='text'  placeholder='Scientific name' name ='scientific_name ' id='scientific_name'
    value={this.scientific_name} onChange={this.changeScientific_name}/><br/>
        </div>


    </form>
    <div className="edtbtnDrug">
    <button type="button" onClick={this.addDrg}>add</button>

    <Link to={"/Patients"}><button id="btnn" type="button" >cancel</button></Link>

    </div>
    </div>
    </div>

)
}}
export default AddDrug;
