import React , {Component} from 'react';
import axios from 'axios';
class PostForm extends Component {





submitHendler=(e)=>{

e.preventDefault();
const formData=new FormData();
formData.append('name',document.querySelector('#fname').value);
formData.append('birthdate',document.querySelector('#fbirthdate').value);
formData.append('wilaya',document.querySelector('#fwilaya').value);
formData.append('phone',document.querySelector('#fphone').value);
formData.append('photo',document.querySelector('#fphoto').files[0]);
console.log(this.state)
axios.post('http://127.0.0.1:8000/patients/', formData)
.then(response=>{
  console.log(response)
})
.catch (err=>{
  console.log(err)
})

}
render (){
const {name,birthdate,wilaya,phone,photo}=this.state
return(
<div>
<form onSubmit={this.submitHendler}>
<div><input type="text" name="fname" value={name} id="fname" />
</div>
<div><input type="date" name="birthdate"value={birthdate} id="fbirthdate" />
</div>
<div><input type="text" name="wilaya"value={wilaya} id="fwilaya"/>
</div>

<div><input type="numbrer" name="phone"value={phone} id="fphone" /></div>

<div>
<input type="file" name="photo" value={photo} id ="fphoto" />
</div>
<button type="submit">submit</button>
</form>
</div>
)


}

}
export default PostForm;
