import React, { Component } from "react";

class Pres extends Component {
  state={
     inputList:[{ firstName: "", lastName: "" }],

   }


  // handle input change
   handleInputChange = (e, index) => {
    const { name, value } = e.target;
    const list = [...inputList];
    list[index][name] = value;
    this.setState({inputList:list})

  };

  // handle click event of the Remove button
   handleRemoveClick = index => {
    const list = [...this.inputList];
    list.splice(index, 1);
    this.setState({inputList:list})

  };

  // handle click event of the Add button
   handleAddClick = () => {
    this.setState([...this.inputList, { firstName: "", lastName: "" }])

  };
render(){
  const {inputList}= this.state
  return (
    <div className="App">
      <h3><a href="https://cluemediator.com">Clue Mediator</a></h3>
      {inputList.map((x, i) => {
        return (
          <div className="box">
            <input
              name="firstName"
   placeholder="Enter First Name"
              value={x.firstName}
              onChange={e => this.handleInputChange(e, i)}
            />
            <input
              className="ml10"
              name="lastName"
   placeholder="Enter Last Name"
              value={x.lastName}
              onChange={e => this.handleInputChange(e, i)}
            />
            <div className="btn-box">
              {inputList.length !== 1 && <button
                className="mr10"
                onClick={() => this.handleRemoveClick(i)}>Remove</button>}
              {inputList.length - 1 === i && <button onClick={this.handleAddClick}>Add</button>}
            </div>
          </div>
        );
      })}
      <div style={{ marginTop: 20 }}>{JSON.stringify(inputList)}</div>
    </div>
  );
}}

export default Pres;
