import React , {Component} from 'react';

import axios from 'axios';
import './Drugs.css'
import Swal from'sweetalert2';
import AddModal from './AddModal'
import {Link} from "react-router-dom"
import Table from 'react-bootstrap/Table'

class Drugs extends Component {

  state={
     drg:[],
   }

   componentDidMount=()=>{
     axios.get("http://127.0.0.1:8000/drugs/")
     .then((response)=>{
       console.log(response)
       this.setState({  drg:response.data})
     })}
render(){
const {drg}=this.state

return(
  <div className='drg'>
<div className="hede">
<h3> Drugs </h3>
</div>
<hr/>
<div className='alldrg'>
<div className='left-dr'>
<Table  striped bordered hover>
<thead>
<tr className="tableHeader" tableHeader>

<th colSpan="3"><div className='titlehederapp'><div>Commercial name</div><div className='fste' >Scientific name</div></div></th>


</tr>
</thead>
<tbody>
{
drg.map(drg=>
<tr key={drg.id}>
<td>{drg.commercial_name}</td>
<td className='col2'>{drg.scientific_name}</td>
<td><div className='icondeletApp'><i class="fas fa-trash-alt" ></i> </div></td>
</tr>
)
}
</tbody>
</Table>
</div>
<div className='right-dr'>
<form onSubmit={this.AddDrugs}>
<div>   <input type='text' placeholder='commercial_name' name='commercial_name' value={this.commercial_name} onChange={this.changeCommercial_name}/></div>
<div>   <input  type='text'  name ='scientific_name 'value={this.scientific_name} onChange={this.changeScientific_name}/></div>
<div>   <button type='submit'>Add</button></div>

</form>
</div>
</div>
</div>
)
}}
export default Drugs;
