import React , {Component} from 'react';
import './Patient.css'
import axios from 'axios';
import AddModalConsul from './AddModalConsul'




class Patient extends Component {

  state={
     posts:[],
     consul:[],
     pres:[],
   show:false
   }



     componentDidMount=()=>{
       axios.get("http://127.0.0.1:8000/prescription_fields/")
       .then((responseee)=>{
         this.setState({  pres:responseee.data})})
       axios.get("http://127.0.0.1:8000/patients/80/")
       .then((response)=>{
         this.setState({  posts:response.data})
       axios.get("http://127.0.0.1:8000/consultations/")
       .then((responsee)=>{
         this.setState({  consul:responsee.data})

         })

       })}

       closeModalhendler=()=>{
         this.setState({
           show:false
         })
       }
       openModalhendler=()=>{
         this.setState({
           show:true
         })
       }
render(){

  const {posts}= this.state



return(

<div  ClassName="patientinfoo">


  {

    <ul className="patient"key={posts.id}>
        <li> <img className="imagePatent" src={posts.photo} /></li>
        <div className='listinfo'>
        <p>name</p>
        <li >{posts.name} </li>
        <p>Birthdate</p>
        <li >{posts.birthdate}</li>
        <p>phone</p>
        <li >{posts.phone}</li>
        <p>wilaya</p>
        <li> {posts.wilaya}</li>

</div>
    </ul>
  }




</div>

)
}}
export default Patient;
