import React,{Component} from 'react'
import axios from 'axios';
import './PostForm.css'
import Patients from './Patients'

class PostForm extends Component {
  constructor(props){
    super(props)
}
    submitHendler=(e)=>{
            const url = 'http://127.0.0.1:8000/patients/'
            const formData = new FormData();

            formData.append('name', 'test name');
            formData.append('birthdate', '2021-05-09');
            formData.append('wilaya', 'chlef');
            formData.append('phone', '0550');
            formData.append('photo', document.querySelector('#photo').files[0]);

            axios.post(url, formData)
            .then(resp => {
                console.log(resp.data);
            });
        }



  render(){
const {name,birthdate,wilaya,phone,photo}=this.state
return(
  <div >

  <form onClick={this.submitHendler}className='PostFormmCons' >


<div className='pst' >
   <label for='name'> name:</label>
    <input type='text'name='name' id='name'  value={name} onChange={this.changeHendlerName}/>
    </div>
    <div className='pst' >
    <label for='dat'> la date : </label>
    <input type='date'name='birthdate' value={birthdate} onChange={this.changeHendlerbirthDate}/>
    </div>
    <div className='pst' >
     <label for='dat'> wilaya : </label>
    <input type='text'name='wilaya' value={wilaya} onChange={this.changeHendlerWilaya}/>
      </div>
      <div className='pst' >
    <label for='dat'> phone : </label>
    <input type='text'name='phone' value={phone} onChange={this.changeHendlerPhone}/>
   </div>
   <div>
   <label for='photo'> photo: </label>
   <input type='file'name='photo' value={photo} onChange={this.changeHendlerPhoto}/>
  </div>

    <button type='submit' >submit</button>

  </form>

  </div>


)}
}
export default PostForm;
