import axios from 'axios';
import './PostForm.css'
import React ,{Component} from 'react'
class PostForm extends Component {

           add=(e)=>{
              const url = 'http://127.0.0.1:8000/patients/'
              const formData = new FormData();

              formData.append('name', document.querySelector('#name').value);
              formData.append('birthdate', document.querySelector('#birthdate').value);
              formData.append('wilaya', document.querySelector('#wilaya').value);
              formData.append('phone', document.querySelector('#phone').value);
              formData.append('photo', document.querySelector('#photo').files[0]);

              axios.post(url, formData)
              .then(resp => {
                  console.log(resp.data);
              });


          }

render(){

  return (

    <div className='PostForm'>
     <form className='pst'onSubmit={this.submitHendler} ><br/>
<label for="name">name</label>
       <input type="text" id="name" placeholder="name"/><br/>

       <input type="date" id="birthdate"placeholder="date"/><br/>

       <input type="text" id="wilaya" placeholder="wilaya"/><br/>

       <input type="text" id="phone" placeholder="phone"/><br/>

        <input type="file" id="photo" placeholder="photo"/><br/>
    </form>
    <button type="button" onClick={this.add}>add</button>
    </div>

  )}
}

export default PostForm;
