import React , {Component} from 'react';
import "./patienteee.css"
import axios from 'axios';
import AddModalConsul from './AddModalConsul'
import {Link} from "react-router-dom"



class Patient extends Component {

  state={
     posts:[],
     consul:[],
     consule:[],
     pres:[],
     c:'',
   show:false
   }
changeB=(e)=>{this.setState({c:e.target.value})}
closeModalhendler=()=>{
     this.setState({show:false})
   }
   openModalhendler=()=>{
     this.setState({show:true})
}

     componentDidMount=()=>{

       axios.get("http://127.0.0.1:8000/patients/"+this.props.match.params.id+"/")
       .then((response)=>{

      this.setState({consul:response.data.consultations,
      posts:response.data,

    })


         })



       }



render(){

  const {posts,consul,pres,show}= this.state
const c=posts.name
console.log(posts)

return(
<div className='cons'>




<div className="left-sid">

 <div className="hederConsul">



<h3> Consultations</h3>
<button className="addCons" onClick={this.openModalhendler} >Add</button>

</div>
<hr/>


<div>
{
consul.map(consul=>
<div className='cns' key={consul.id}>
<div><u>{consul.date}</u></div>
<div><h4>Type</h4><p>{consul.type}</p></div>
<div><h4>description</h4><p>{consul.description}</p></div>
<div><h4>outcome</h4><p>{consul.outcome}</p></div>

</div>  )}
</div>




</div>

<div className='patientinfoo'>


{

  <ul className="patient"key={posts.id}>
      <li> <img className="imagePatient" src={posts.photo} /></li>
      <div className='listinfo'>
      <p>name</p>
      <li >  <input type="text" value={c} onChange={this.changeB} /></li>
      <p>Birthdate</p>
      <li ><input type="date" defaultValue={posts.birthdate} onChange={this.changeB} /></li>
      <p>phone</p>
      <li ><input type="text" defaultValue={posts.phone} /></li>
      <p>wilaya</p>
      <li> <input type="text" defaultValue={posts.wilaya} /></li>
     <i class="far fa-edit" onClick={this.edit}></i>
   </div>
  </ul>
}



</div>
{
  show ? <div className="back-drop"></div> :null
}
<AddModalConsul show={show} closeModalhendler={this.closeModalhendler}/>
</div>

)
}}
export default Patient;
