import React, { Component } from 'react';
import Container from '@material-ui/core/Container';
import TextField from '@material-ui/core/TextField';
import Button from '@material-ui/core/Button';
import IconButton from '@material-ui/core/IconButton';
import RemoveIcon from '@material-ui/icons/Remove';
import AddIcon from '@material-ui/icons/Add';
import Icon from '@material-ui/core/Icon';
import { v4 as uuidv4 } from 'uuid';





class Pres extends Component {
  state={
    inputFields:[{ id: uuidv4(), firstName: '', lastName: '' }]
       }


   handleSubmit = (e) => {
    e.preventDefault();

  };

   handleChangeInput = (id, event) => {
    const newInputFields = this.inputFields.map(i => {
      if(id === i.id) {
        i[event.target.name] = event.target.value
      }
      return i;
    })

  this.setState=(  {inputFields:newInputFields})  ;
  }

   handleAddFields = () => {
    setInputFields([...inputFields, { id: uuidv4(),  firstName: '', lastName: '' }])
  }

   handleRemoveFields = id => {
    const values  = [...inputFields];
    values.splice(values.findIndex(value => value.id === id), 1);
    setInputFields(values);
  }
render(){
  return (
    <Container>
      <h1>Add New Member</h1>
      <form  onSubmit={handleSubmit}>
        { inputFields.map(inputField => (
          <div key={inputField.id}>
            <input
              name="firstName"
              label="First Name"

              value={inputField.firstName}
              onChange={event => handleChangeInput(inputField.id, event)}
            />
            <input
              name="lastName"
              label="Last Name"

              value={inputField.lastName}
              onChange={event => this.handleChangeInput(inputField.id, event)}
            />
            <IconButton disabled={this.inputFields.length === 1} onClick={() => this.handleRemoveFields(this.inputField.id)}>
              <RemoveIcon />
            </IconButton>
            <IconButton
              onClick={this.handleAddFields}
            >
              <AddIcon />
            </IconButton>
          </div>
        )) }
        <Button

          variant="contained"
          color="primary"
          type="submit"
          endIcon={<Icon>send</Icon>}
          onClick={this.handleSubmit}
        >Send</Button>
      </form>
    </Container>
  );
}}
export default Pres;
