import React , {Component} from 'react';
import axios from 'axios';



class PostForm extends Component {
  constructor(props){
    super(props)
    this.state={namee:'',
                birthdate:'',
                wilaya:'',
                phone:'',
                photo:'',
    }
  }

submithendler(){
  console.log(this.state)
}


render(){
  const {name,birthdate,wilaya,phone,photo}= this.state
return (

<div>
<form onSubmit={this.submithendler}>
  <div>
  <input type ='text' name ='name' value={this.state.name} onChange={(data)=>{this.setState({name:data.target.value})}}/>
  </div>
  <div>
  <input type ='date' name ='birthdate' value={this.state.birthdate}  onChange={(data)=>{this.setState({birthdate:data.target.value})}} />
  </div>
  <div>
  <input type ='text' name ='wilaya' value={this.state.wilaya}  onChange={(data)=>{this.setState({wilaya:data.target.value})}}/>
  </div>
  <div>
  <input type ='number' name ='phone' value={this.state.phone}  onChange={(data)=>{this.setState({phone:data.target.value})}}/>
  </div>
  <div>
  <input type ='file' name ='photo' value={this.state.photo}  onChange={(data)=>{this.setState({photo:data.target.value})}}/>
  </div>
<button type='submit'>submit</button>
</form>

</div>



)


}


}
export default PostForm;
