import React , {Component} from 'react';
import axios from 'axios';
import './Patients.css';
import Swal from'sweetalert2';
class Patients extends Component {
  state={
     posts:[]
   }
handelDelet=(patientId)=>{
  Swal.fire({
  title: 'Are you sure?',
  text: "You won't be able to revert this!",
  icon: 'warning',
  showCancelButton: true,
  confirmButtonColor: '#3085d6',
  cancelButtonColor: '#d33',
  confirmButtonText: 'Yes, delete it!'
}).then((result) => {
  if (result.isConfirmed) {
    Swal.fire(
      deletpatient(patientId)
    )
  }
})
}
deletpatient=(patientId)=>{
     axios.delete("http://127.0.0.1:8000/patients/"+patientId+"/")
     .then(response=>{
       if (response.data =! null){
         alert('deleted')
         this.setState({
        posts : this.state.posts.filter(posts=> posts.id==!patientId)})
       }
     })
    }




   componentDidMount=()=>{
     axios.get("http://127.0.0.1:8000/patients/")
     .then((response)=>{
       console.log(response)
       this.setState({posts:response.data})
     })}

render(){

  const {posts}= this.state
return(
<div>
<div className="hederPatient">
  <h3>Patents list</h3>
   <button className="addpatient" >add</button>
  </div>
  <hr/>
  {
  posts.map(posts=>
    <ul className="patient"key={posts.id}>
        <li> <img className="imagePatent" src={posts.photo} /></li>
        <li className="name">{posts.name} <div className="wilaya">{posts.wilaya}</div> </li>
        <li className="phone"> <div className="phoneCouleur">phone</div> {posts.phone}</li>
        <li className="idPatient"> <div className="IdCouleur">ID</div>{posts.id}</li>
        <li className="icondelet"><i class="fas fa-trash-alt" onClick={this.handelDelet.bind(this,posts.id)}></i></li>

    </ul>)
  }
</div>
)
}}
export default Patients;
