import React , {Component} from 'react';
import './Patient.css'
import axios from 'axios';





class Patient extends Component {

  state={
     posts:[],
     consul:[]
   }



     componentDidMount=()=>{
       axios.get("http://127.0.0.1:8000/consultations/")
       .then((responsee)=>{
         this.setState({  consul:responsee.data})
         axios.get("http://127.0.0.1:8000/patients/51/")
         .then((response)=>{

           this.setState({  posts:response.data})
         })

       })}

render(){

  const {posts,consul}= this.state
return(
  <div className="cons">
  <div className='left-sid'>

  <div className="hederPatiente">
    <h3>Consultations</h3>
    <button className="addpatient" >Add</button>

     </div><hr/>
     <div className='consultations'>
     {
     consul.map(consul=>
       <div key={consul.id}>
        <div> <h4>date</h4> {consul.date}</div>
          <div> <h3>Type</h3>{consul.type}</div>

          <div> <h3>la description</h3>{consul.description}</div>
          <div><h3>outcom</h3>{consul.outcom}</div>

       </div>)
     }
     </div>
  </div>
<div  ClassName="patientinfoo">


  {

    <ul className="patient"key={posts.id}>
        <li> <img className="imagePatent" src={posts.photo} /></li>
        <div className='listinfo'>
        <p>name</p>
        <li >{posts.name} </li>
        <p>Birthdate</p>
        <li >{posts.birthdate}</li>
        <p>phone</p>
        <li >{posts.phone}</li>
        <p>wilaya</p>
        <li> {posts.wilaya}</li>

</div>
    </ul>
  }


           </div>
</div>
)
}}
export default Patient;
