import React,{Component} from 'react'
import axios from 'axios';
import './PostForm.css'
import Patients from './Patients'

class PostForm extends Component {
  constructor(props){
    super(props)
    this.state={
         name:"",
         birthdate:"",
         wilaya:"",
         phone:"",
         photo:"",
            }
  }

  hendelUserName=(e)=>{
    this.setState({
      name:e.target.value
    })
  }
  hendelbirthdate=(e)=>{
    this.setState({birthdate:e.target.value})
  }
  hendelwilaya=(e)=>{
    this.setState({wilaya:e.target.value})
  }
  hendelphone=(e)=>{
    this.setState({phone:e.target.value})
  }
hendelphoto=(e)=>{
    this.setState({photo:e.target.files[0]})
}

  hendelSubmit=(e)=>{

    e.preventDefault()
    console.log(this.state)
    axios.post('http://127.0.0.1:8000/patients/',this.state)

  }
  render(){
const {name,birthdate,wilaya,phone}=this.state
return(

<div className='PostForm'>

<form onClick={this.hendelSubmit}className='PostFormm' >
<div className='pst' >
   <label for='name'> name:</label>
    <input type='text'name='name' id='name' placeholder='name' value={this.state.name} onChange={this.hendelUserName}/>
    </div>
    <div className='pst' >
    <label for='dat'> la date : </label>
    <input type='date'name='birthdate' value={this.state.birthdate} onChange={this.hendelbirthdate}/>
    </div>
    <div className='pst' >
     <label for='dat'> wilaya : </label>
    <input type='text'name='wilaya' value={this.state.wilaya} onChange={this.hendelwilaya}/>
      </div>
      <div className='pst' >
    <label for='dat'> phone : </label>
    <input type='text'name='phone' value={this.state.phone} onChange={this.hendelphone}/>
   </div>
   <div className='pst'>
   <label for='ph'> photo </label>
   <input type='file'name='photo'id='ph' value={this.state.photo} onChange={this.hendelphoto}/>
   </div>
    <button type='submit' >submit</button>

</form>

</div>


)}
}
export default PostForm;
