import React,{Component} from 'react'
import axios from 'axios';


class PostForm extends Component {
  constructor(props){
    super(props)
    this.state={
         name:"",
         birthdate:"",
         wilaya:"",
         phone:"",
            }
  }

  hendelUserName=(e)=>{
    this.setState({
      name:e.target.value
    })
  }
  hendelbirthdate=(e)=>{
    this.setState({birthdate:e.target.value})
  }
  hendelwilaya=(e)=>{
    this.setState({wilaya:e.target.value})
  }
  hendelphone=(e)=>{
    this.setState({phone:e.target.value})
  }
  hendelphoto=(e)=>{
    this.setState({photo:e.target.value.files[0]})
  }

  hendelSubmit=(e)=>{
    e.preventDefault()
    console.log(this.state)
  }

  render(){
const {name,birthdate,wilaya,phone,photo}=this.state
return(

<div>

<form onSubmit={this.hendelSubmit}>
    <input type='text'name='name' value={this.state.name} onChange={this.hendelUserName}/>
    <input type='date'name='birthdate' value={this.state.birthdate} onChange={this.hendelbirthdate}/>
    <input type='text'name='wilaya' value={this.state.wilaya} onChange={this.hendelwilaya}/>
    <input type='text'name='phone' value={this.state.phone} onChange={this.hendelphone}/>
    <input type='file' name='photo' value={this.state.photo} onChange={this.hendelphoto}/>
    <button type='submit'>submit</button>
</form>

</div>


)}
}
export default PostForm;
