import axios from 'axios';
import {Link} from "react-router-dom"
import React ,{Component} from 'react'
import './Editform.css'
class EditApp extends Component {

  state={
     posts:[],

   }
   add=(e)=>{
      const url = 'http://127.0.0.1:8000/appointments/'+this.props.match.params.id+"/"
      const formData = new FormData();

      formData.append('name', document.querySelector('#name').value);
      formData.append('time', document.querySelector('#time').value);



      axios.put(url, formData)
      .then(resp => {
          console.log(resp.data);


      });


  }



   componentDidMount=()=>{
     axios.get("http://127.0.0.1:8000/appointments/"+this.props.match.params.id+"/")
     .then((response)=>{
       console.log(response)
       this.setState({  posts:response.data})

     })}

render(){
    const {posts}= this.state

  return (

  <div className="edt">
  <div className="hededt">
  <h2>modifier patient</h2>

  </div>
  <div className='edtForm'>
   <form className='pst' ><br/>
   <div className="inputtfield">
<label for="patient_name">patient_name: </label>
     <input  className="input" defaultValue={posts.patient_name}type="text" id="name"/><br/>
     </div>
     <div className="inputtfield">
<label for="time">time: </label>
     <input className="input"type="time" defaultValue={posts.birthdate} id="time"/><br/>
      </div>



  </form>
  <div className="edtbtn">
  <Link to={"/Appointment"}><button type="button" onClick={this.add}>enregistrer</button></Link>
  <Link to={"/Appointment"}><button id="btnn" type="button" >cancel</button></Link>
  </div>
  </div>
    </div>

  )}
}

export default EditApp;
