import React,{Component} from 'react'
import './AddModal.css'
import PostForm from './PostForm'


 const AddModal =({show,closeModalhendler,hendelSubmit})=> {
   return (
<div className="modal-wrapper"
style={{
  opacity: show ? '1':'0',
  background: show ? 'rgba(63,61,61,0.692)':'#eee'
}}
>
<div className="hederModal">
<h2>add new patient</h2>
<span className="closeModalBtn" onClick={closeModalhendler}>x</span>
</div>

<div className="modalContainer">
<div className="bodyModal">
<PostForm/>
</div>


</div>
</div>
)
}

export default AddModal;
