import React , {Component} from 'react';
import Axios from 'axios';



class PostForm extends Component {
  constructor(props){
    super(props)
    this.state={namee:'',
                birthdate:'',
                wilaya:'',
                phone:'',
                photo:'',
    }
  }
hendelchange =(e)=>{
this.setState({[e.target.name]:e.target.value})}

submithendler=(e)=>{
  e.preventDefault()
  console.log (this.state)

}


render(){
  const {name,birthdate,wilaya,phone,photo}= this.state
return (

<div>
<form onSubmit={this.submithendler}>
  <div>
  <input type ='text' name ='namee' value={name} onChange={this.hendelchange}/>
  </div>
  <div>
  <input type ='date' name ='birthdate' value={birthdate} onChange={this.hendelchange} />
  </div>
  <div>
  <input type ='text' name ='wilaya' value={wilaya} onChange={this.hendelchange} />
  </div>
  <div>
  <input type ='number' name ='phone' value={phone} onChange={this.hendelchange}/>
  </div>
  <div>
  <input type ='file' name ='photo' value={photo} onChange={this.hendelchange}/>
  </div>
<button type='submit'>submit</button>
</form>

</div>



)


}


}
export default PostForm;
