      </div>
      <div className='pst' >
    <label for='dat'> phone : </label>
    <input type='text'name='phone' value={this.state.phone} onChange={this.hendelphone}/>
   </div>

<div>
<input type='file' value={this.state.photo} onChange={this.hendelphoto}/>
</div>
    <button type='submit' >submit</button>

  </form>

  </div>


)}
}
export default PostForm;
