import React , {Component} from 'react';
import axios from 'axios';
import './Patients.css'

class Patients extends Component {
  state={
     posts:[]
   }
deletpatient=()=>{
axios.delet("http://127.0.0.1:8000/patients/2")
.then((response)=>{
  if(response.data !=null){
    alert('delet succesfuly')
  }
})


};
   componentDidMount=()=>{
     axios.get("http://127.0.0.1:8000/patients/")
     .then((response)=>{
       console.log(response)
       this.setState({posts:response.data})
     })}

render(){

  const {posts}= this.state
return(
<div>
<div className="hederPatient">
  <h3>Patents list</h3>
   <button className="addpatient" >add</button>
  </div>
  <hr/>
  {
  posts.map(posts=><ul className="patient"key={posts.id}>
    <li>  name : {posts.name} </li>

    <li> id : {posts.id}</li>

    <li className="icondelet"><i class="fas fa-trash-alt" onClick={this.deletpatient.bind(this,posts.id)}></i></li>

    </ul>)
  }
</div>
)
}}
export default Patients;
