import React,{Component} from 'react'
import axios from 'axios';



class PostFormCons extends Component {

  state={
     posts:[],

   }




  addC=(e)=>{
     const url = 'http://127.0.0.1:8000/consultations/'
     const formData = new FormData();

     formData.append('date', document.querySelector('#date').value);
     formData.append('type', document.querySelector('#type').value);
     formData.append('description', document.querySelector('#description').value);
     formData.append('outcome', document.querySelector('#outcome').value);
     formData.append('patient', document.querySelector('#patient').value);


     axios.post(url, formData)
     .then(resp => {
         console.log(resp.data);
     });


 }

 componentDidMount=()=>{

   axios.get("http://127.0.0.1:8000/patients/")
   .then((response)=>{
     this.setState({  posts:response.data})


     })
    }

  render(){

return(

<div className='PostFormCons'>
<form  className='PostFormmCons' >
<div className='pst' >
   <label for='date'> date:</label>
    <input type='date'id='date'   />
    </div>
    <div className='pst' >
    <label for='type'> type : </label>
    <input type='text'id='type' />
    </div>
    <div className='pst' >
     <label for='description'> description : </label>
    <textarea  id="description"/>
      </div>
      <div className='pst' >
    <label for='outcome'> outcome : </label>
    <textarea id='outcome' />
   </div>
   <div className='pst' >
<label for='patient'>name:</label>
 <select name="patient" id="patient" placeholder='name'>
 {
   this.state.posts.map(posts=>
     <option key={posts.id} value={posts.id}>{posts.name}</option>
   )

 }

 </select>

</div>


</form>
<button type='submit' onClick={this.addC}>submit</button>
</div>


)}
}
export default PostFormCons;
