import React , {Component} from 'react';

import axios from 'axios';
import './Patients.css';
import Swal from'sweetalert2';
import AddModal from './AddModal'



class Patients extends Component {

  state={
     posts:[],
     show:false
   }
  handelDelet=(patientId)=>{
  Swal.fire({
  title: 'Are you sure?',
  text: "You won't be able to revert this!",
  icon: 'warning',
  showCancelButton: true,
  confirmButtonColor: '#3085d6',
  cancelButtonColor: '#d33',
  confirmButtonText: 'Yes, delete it!'
}).then((result) => {
  if (result.isConfirmed) {
  this.deletpatient(patientId)
  }
})
}
 deletpatient=(patientId)=>{
     axios.delete("http://127.0.0.1:8000/patients/"+patientId+"/")
     .then(response=>{
       if (response.data =! null){

      this.setState({
        posts:this.state.posts.filter(posts=>posts.id !== patientId)
      })
    }
  })
}

closeModalhendler=()=>{
  this.setState({
    show:false
  })
}
openModalhendler=()=>{
  this.setState({
    show:true
  })
}




   componentDidMount=()=>{
     axios.get("http://127.0.0.1:8000/patients/")
     .then((response)=>{
       console.log(response)
       this.setState({  posts:response.data})
     })}

render(){

  const {posts,show}= this.state
return(
<div >
<div className="hederPatient">
  <h3>Patents list</h3>
<button className="addpatient" onClick={this.openModalhendler} >Info</button>
  </div>
  <hr/>

   {
     posts.map(posts=>
    <option>{posts.name}</option>

   )

   }


{show ? <div className="back-drop"></div>:null}
  <AddModal show={show} closeModalhendler={this.closeModalhendler}/>
           </div>

)
}}
export default Patients;
