import React, { Component } from 'react'
import axios from 'axios'
import {Link} from "react-router-dom"
class Drugtest extends Component {
state={
  pste:[],
  posts:[],
x:'57'
}


addp=(e,x)=>{
  const url = 'http://127.0.0.1:8000/prescription_fields/'
  const formData = new FormData();

  formData.append('quantity', document.querySelector('#quantity').value);
  formData.append('times_per_day', document.querySelector('#times_per_day').value);
  formData.append('drug', document.querySelector('#drug').value);
  formData.append('consultation',x);

  axios.post(url, formData)
  .then(resp => {

   }
  )
}

componentDidMount=()=>{

  axios.get("http://127.0.0.1:8000/patients/"+95+"/")
  .then((response)=>{
    this.setState({  posts:response.data})
console.log(response.data)

    })
    axios.get("http://127.0.0.1:8000/drugs/")
    .then((res)=>{
      this.setState({  pste:res.data})
      console.log(res.data)


      })

   }

  render(){

const {pste,datee,posts}=this.state
    return(
      <div className='PostFormConsG'>
      <div className="hededt">
      <h2>Add consultations</h2>

      </div>
      <form  className='PostFormmCons' >
      <div className='pste' >
      <label for='patient'>name</label>

      <select name="patient" id="patient">
      {
        <option key={posts.id} value={posts.id}>{posts.name}</option>
      }
      </select>
      </div>

      <div className='pste' >
         <label for='date'> date</label>
        <input  type ='date' id='date'name= 'date'value={datee} onChange={this.dateChange}/>
          </div>
          <div className='pste' >
          <label for='type'> type  </label>
          <input type='text'id='type' />
          </div>
          <div className='pste' >
           <label for='description'> description </label>
          <textarea  id="description"/>
            </div>
            <div className='pste'><label for='outcome'> outcome  </label>
          <textarea id='outcome' />
         </div>
         <div className="input_field">
       <label for="quantity">quantity: </label>
           <input  className="input" type="text" id="quantity" required onChange={this.quantityChangee}/><br/>
           </div>
           <div className="input_field">
       <label for="times_per_day">times_per_day: </label>
           <input className="input"type="text"  id="times_per_day"onChange={this.times_per_dayChangee}/><br/>
            </div>

            <select name="drug" id="drug">
            {
          pste.map(pste=>  <option key={pste.id} value={pste.id} >{pste.commercial_name}</option>)
          }

            </select>



      </form>
      <div className="addbtn">
      <button type="button" onClick={this.addp}>enregistrer</button>
      <Link to={"/Patient/"+posts.id}><button id="btnn" type="button" >cancel</button></Link>
      </div>
      </div>
    )
  }
}
export default Drugtest
