import React,{useState,useEffect} from 'react'
import axios from 'axios';

import {Link} from "react-router-dom"
import Container from '@material-ui/core/Container';
import TextField from '@material-ui/core/TextField';
import Button from '@material-ui/core/Button';
import IconButton from '@material-ui/core/IconButton';
import RemoveIcon from '@material-ui/icons/Remove';
import AddIcon from '@material-ui/icons/Add';
import Icon from '@material-ui/core/Icon';
import { v4 as uuidv4 } from 'uuid';

function Pres () {

   const [inputFields, setInputFields] = useState([
     { id: uuidv4(), firstName: '', lastName: '' },
   ]);

   const handleSubmit = (e) => {
     e.preventDefault();
     console.log("InputFields", inputFields);
   };

   const handleChangeInput = (id, event) => {
     const newInputFields = inputFields.map(i => {
       if(id === i.id) {
         i[event.target.name] = event.target.value
       }
       return i;
     })

     setInputFields(newInputFields);
   }

   const handleAddFields = () => {
     setInputFields([...inputFields, { id: uuidv4(),  firstName: '', lastName: '' }])
   }

   const handleRemoveFields = id => {
     const values  = [...inputFields];
     values.splice(values.findIndex(value => value.id === id), 1);
     setInputFields(values);
   }
useEffect(()=>
   axios.get("http://127.0.0.1:8000/patients/"+match.params.id+"/")
   .then(resp => {
       console.log(resp.data);
setPosts(resp.data)
   })


)
const [posts, setPosts] = useState([]);
const [date,setDate]=useState(new Date().toISOString().split("T")[0])

  const addC=(e)=>{
     const url = 'http://127.0.0.1:8000/consultations/'
     const formData = new FormData();

     formData.append('date', document.querySelector('#date').value);
     formData.append('type', document.querySelector('#type').value);
     formData.append('description', document.querySelector('#description').value);
     formData.append('outcome', document.querySelector('#outcome').value);
     formData.append('patient', document.querySelector('#patient').value);


     axios.post(url, formData)
     .then(resp => {
         console.log(resp.data);
         alert('cons add succesfuly')
     });


 }



  const  dateChangee=(e)=>{

      setDate(e.target.value)
    }


return(



<div className='PostFormConsG'>
<div className="hededt">
<h2>Add consultations</h2>

</div>
<form  className='PostFormmCons' >
<div className='pste' >
<label for='patient'>name</label>
<select>
<option name="patient" id="patient" value={posts.id}>{posts.name} </option>
</select>



</div>

<div className='pste' >
   <label for='date'> date</label>
  <input  type ='date' id='date'name= 'date'value={date} onChange={dateChangee}/>
    </div>
    <div className='pste' >
    <label for='type'> type  </label>
    <input type='text'id='type' />
    </div>
    <div className='pste' >
     <label for='description'> description </label>
    <textarea  id="description"/>
      </div>
      <div className='pste'><label for='outcome'> outcome  </label>
    <textarea id='outcome' />
   </div>

   { inputFields.map(inputField => (
          <div key={inputField.id}>
            <TextField
              name="firstName"
              label="First Name"
              variant="filled"
              value={inputField.firstName}
              onChange={event => handleChangeInput(inputField.id, event)}
            />
            <TextField
              name="lastName"
              label="Last Name"
              variant="filled"
              value={inputField.lastName}
              onChange={event => handleChangeInput(inputField.id, event)}
            />
            <IconButton disabled={inputFields.length === 1} onClick={() => handleRemoveFields(inputField.id)}>
              <RemoveIcon />
            </IconButton>
            <IconButton
              onClick={handleAddFields}
            >
              <AddIcon />
            </IconButton>
          </div>
        )) }
        <Button

          variant="contained"
          color="primary"
          type="submit"
          endIcon={<Icon>send</Icon>}
          onClick={handleSubmit}
        >Send</Button>

</form>
<div className="addbtn">
<button type="button" onClick={addC}>enregistrer</button>
<Link to={"/Patient/"+posts.id}><button id="btnn" type="button" >cancel</button></Link>
</div>

</div>


)
}
export default Pres;
