import React , {Component} from 'react';
import axios from 'axios';
class PostForm extends Component {

 constructor(props){
  super(props)
  this.state={
name:"",
birthdate:"",
wilaya:"",
phone:"",
photo:"",

  }

const formData = new formData();
}
changeHandler=(e)=>{
formData.append(e.target.name,e.target.value)
}
changeHandlerphoto=(e)=>{
  formData.append(e.target.name,e.target.files[0])
}


submitHendler=(e)=>{
e.preventDefault()
console.log(this.state)
axios.post('http://127.0.0.1:8000/patients/', formData)
.then(response=>{
  console.log(response)
})
.catch (err=>{
  console.log(err)
})

}
render (){
const {name,birthdate,wilaya,phone,photo}=this.state
return(
<div>
<form onSubmit={this.submitHendler}>
<div><input type="text" name="name" value={name} onChange={this.changeHandler}/>
</div>
<div><input type="date" name="birthdate"value={birthdate} onChange={this.changeHandler}/>
</div>
<div><input type="text" name="wilaya"value={wilaya} onChange={this.changeHandler}/>
</div>

<div><input type="numbrer" name="phone"value={phone} onChange={this.changeHandler}/>
</div>

<div>
<input type="file" name="photo" value={photo} onChange={this.changeHandlerphoto}/>
</div>
<button type="submit">submit</button>
</form>
</div>
)


}

}
export default PostForm;
