import React , {Component} from 'react';
import  './patienteee.css'
import axios from 'axios';
import AddModalConsul from './AddModalConsul'




class Patient extends Component {

  state={
     posts:[],
     consul:[],
     pres:[],
   show:false
   }



     componentDidMount=()=>{
       axios.get("http://127.0.0.1:8000/prescription_fields/")
       .then((response)=>{
         this.setState({  pres:response.data})

         })
       axios.get("http://127.0.0.1:8000/patients/74/")
       .then((response)=>{
         this.setState({  posts:response.data})


         })
         axios.get("http://127.0.0.1:8000/consultations/?patient=77")
         .then((response)=>{
           this.setState({  consul:response.data})


           })

       }

      

render(){

  const {posts,consul,pres,show}= this.state



return(
<div className='cons'>




<div className="left-sid">

 <div className="hederConsul">



<h3> Consultations</h3>
<button className="addCons" onClick={this.openModalhendler} >Add</button>

</div>
<hr/>


<div className='list-con'>

{
consul.map(consul=>
<div className='cns' key={consul.id}>
<div><u>{consul.date}</u></div>
<div><h4>Type</h4><p>{consul.type}</p></div>
<div><h4>description</h4><p>{consul.description}</p></div>
<div><h4>outcome</h4><p>{consul.outcome}</p></div>
<div>{
pres.map(pres=>
<div>{pres.quantity}</div>
)
}</div>
</div>  )
}



</div>

</div>







<div className='patientinfoo'>


{

  <ul className="patient"key={posts.id}>
      <li> <img className="imagePatent" src={posts.photo} /></li>
      <div className='listinfo'>
      <p>name</p>
      <li >{posts.name} </li>
      <p>Birthdate</p>
      <li >{posts.birthdate}</li>
      <p>phone</p>
      <li >{posts.phone}</li>
      <p>wilaya</p>
      <li> {posts.wilaya}</li>

   </div>
  </ul>
}



</div>
{
  show ? <div className="back-drop"></div> :null
}
<AddModalConsul show={show} closeModalhendler={this.closeModalhendler}/>
</div>

)
}}
export default Patient;
