import React , {Component} from 'react';

import axios from 'axios';




class Appointment extends Component {
constructor(props){
  super(props)
  this.state={
    date:"",
    resdate:[]
  }
}
dateChange=(e)=>{
  this.setState({date:e.target.value})
}
componentDidMount=()=>{
  axios.get("http://127.0.0.1:8000/appointments/")
  .then((response)=>{
    console.log(response)
    this.setState({  resdate:response.data})
  })}


render(){
const{resdate,date}=this.state

return(
<div >
<div className="hederPatient">
  <h3>Appointment list</h3>
  <div>
  <input type ='date' name= 'date'value={date} onChange={this.dateChange}/>
<button className="searchDate" >go</button>
</div>
  </div>
  <hr/>
  <div>
  { resdate.map(resdate=>
       <ul className="patient"key={resdate.id}>

        <li className="PateintName">{resdate.patient_name}  </li>
        <li className="Time">  {resdate.time}</li>
        <li className="icondelet"><i class="fas fa-trash-alt" ></i></li>

    </ul>
  )
  }

  </div>

           </div>

)
}}
export default Appointment;
