import React , {Component} from 'react';
import './Patient.css'
import axios from 'axios';





class Patient extends Component {

  state={
     posts:[],
     consul:[],
     pres:[]
   }



     componentDidMount=()=>{
       axios.get("http://127.0.0.1:8000/prescription_fields/")
       .then((responseee)=>{
         this.setState({  pres:responseee.data})})
       axios.get("http://127.0.0.1:8000/patients/51/")
       .then((response)=>{
         this.setState({  posts:response.data})
       axios.get("http://127.0.0.1:8000/consultations/")
       .then((responsee)=>{
         this.setState({  consul:responsee.data})

         })

       })}

render(){

  const {posts,consul,pres}= this.state
  if(posts.id===consul.patient){
    message =  <div className='consultations'>
     {
     consul.map(consul=>

       <div className='cns'key={consul.id}>
           <div className='list-con'> <u>{consul.date}</u></div>
           <div className='list-con'> <h4>Type</h4><p>{consul.type}</p></div>
           <div className='list-con'> <h4>la description</h4><p>{consul.description}</p></div>
           <div className='list-con'><h4>outcome</h4><p>{consul.outcome}</p></div>
           {
             pres.map(pres=>
               <div>
            <p> la quntity{pres.quantity}</p>
              <p> time par day{pres.times_per_day}</p>
              </div>
             )
           }
       </div>)

     }

     </div>
  }
return(
  <div className="cons">

  <div className='left-sid'>

  <div className="hederPatiente">
    <h3>Consultations</h3>
    <button className="addpatient" >Add</button>

     </div><hr/>
 {message}

  </div>
<div  ClassName="patientinfoo">


  {

    <ul className="patient"key={posts.id}>
        <li> <img className="imagePatent" src={posts.photo} /></li>
        <div className='listinfo'>
        <p>name</p>
        <li >{posts.name} </li>
        <p>Birthdate</p>
        <li >{posts.birthdate}</li>
        <p>phone</p>
        <li >{posts.phone}</li>
        <p>wilaya</p>
        <li> {posts.wilaya}</li>

</div>
    </ul>
  }


           </div>
</div>
)
}}
export default Patient;
