import React, { Component } from 'react'
import axios from 'axios'
class Drugtest extends Component {
state={
  p:[]
}

componentDidMount=()=>{
axios.get("http://127.0.0.1:8000/drugs/")
.then((res)=>{
  this.setState({  p:res.data})
console.log(res.data)

})}

  render(){

const {p}=this.state
    return(
      <div>
      <select name="drug" id="drug">
      {
        p.map(p=>  <option key={p.id} value={p.id}>{p.commercial_name}</option>)

      }

      </select>


      </div>
    )
  }
}
export default Drugtest
