import React, { useState } from 'react';
import Container from '@material-ui/core/Container';
import TextField from '@material-ui/core/TextField';
import Button from '@material-ui/core/Button';
import IconButton from '@material-ui/core/IconButton';
import RemoveIcon from '@material-ui/icons/Remove';
import AddIcon from '@material-ui/icons/Add';
import axios from 'axios';
import Icon from '@material-ui/core/Icon';
import { v4 as uuidv4 } from 'uuid';

import { makeStyles } from '@material-ui/core/styles';

const useStyles = makeStyles((theme) => ({
  root: {
    '& .MuiTextField-root': {
      margin: theme.spacing(1),
    },
  },
  button: {
    margin: theme.spacing(1),
  }
}))

function App() {
  const classes = useStyles()
  const [inputFields, setInputFields] = useState([
    { id: uuidv4(), quantity: '', times_per_day: '',consultation: '',drug: '' },
  ]);
const  handleSubmit=(e)=>{
     e.preventDefault();
     const url = 'http://127.0.0.1:8000/prescription_fields/'
     const formData = new FormData();

     formData.append('quantity', document.querySelector('#quantity').value);
     formData.append('times_per_day', document.querySelector('#times_per_day').value);
     formData.append('consultation', document.querySelector('#consultation').value);
     formData.append('drug', document.querySelector('#drug').value);

     axios.post(url, formData)
     .then(resp => {
         console.log(resp.data);
         alert("new patient")

     });


 }
  const add = (e) => {
    e.preventDefault();
    console.log("InputFields", inputFields);
    console.log(inputFields[0])
  };

  const handleChangeInput = (id, event) => {
    const newInputFields = inputFields.map(i => {
      if(id === i.id) {
        i[event.target.name] = event.target.value
      }
      return i;
    })

    setInputFields(newInputFields);
  }

  const handleAddFields = () => {
    setInputFields([...inputFields, { id: uuidv4(),  quantity: '', times_per_day: '',consultation: '',drug: '' }])
  }

  const handleRemoveFields = id => {
    const values  = [...inputFields];
    values.splice(values.findIndex(value => value.id === id), 1);
    setInputFields(values);
  }

  return (
    <Container>
      <h1>Add New Member</h1>
      <form className={classes.root} onSubmit={handleSubmit}>
        { inputFields.map(inputField => (
          <div key={inputField.id}>
            <TextField
              name="quantity"
              id='quantity'
              label="quantity"
              variant="filled"
              value={inputField.quantity}
              onChange={event => handleChangeInput(inputField.id, event)}
            />
            <TextField
              name="times_per_day"
              id='times_per_day'
              label="times_per_day"
              variant="filled"
              value={inputField.times_per_day}
              onChange={event => handleChangeInput(inputField.id, event)}
            />
            <TextField
              name="consultation"
              id='consultation'
              label="consultation"
              variant="filled"
              value={inputField.consultation}
              onChange={event => handleChangeInput(inputField.id, event)}
            />
            <TextField
              name="drug"
              id='drug'
              label="drug"
              variant="filled"
              value={inputField.drug}
              onChange={event => handleChangeInput(inputField.id, event)}
            />
            <IconButton disabled={inputFields.length === 1} onClick={() => handleRemoveFields(inputField.id)}>
              <RemoveIcon />
            </IconButton>
            <IconButton
              onClick={handleAddFields}
            >
              <AddIcon />
            </IconButton>
          </div>
        )) }
        <Button
          className={classes.button}
          variant="contained"
          color="primary"
          type="submit"
          endIcon={<Icon>send</Icon>}
          onClick={handleSubmit}
        >Send</Button>
      </form>
    </Container>
  );
}

export default App;
